import React, { useState } from 'react';
import {
  Container,
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  TextField,
  InputAdornment,
  CssBaseline,
  createTheme,
  ThemeProvider,
  Tabs,
  Tab
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import ClothingList from './components/ClothingList';
import ClothingForm from './components/ClothingForm';
import OutfitCreator from './components/OutfitCreator';

const theme = createTheme({
  palette: {
    primary: {
      main: '#ff69b4', // Updated to a Clueless-inspired pink
    },
    secondary: {
      main: '#f50057',
    },
  },
});

function App() {
  const [openForm, setOpenForm] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentTab, setCurrentTab] = useState(0);

  const handleAddClick = () => {
    setEditItem(null);
    setOpenForm(true);
  };

  const handleEditItem = (item) => {
    setEditItem(item);
    setOpenForm(true);
  };

  const handleCloseForm = (refresh) => {
    setOpenForm(false);
    setEditItem(null);
  };

  const handleTabChange = (event, newValue) => {
    setCurrentTab(newValue);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Closet Organizer
            </Typography>
            {currentTab === 0 && (
              <Button
                color="inherit"
                startIcon={<AddIcon />}
                onClick={handleAddClick}
              >
                Add Item
              </Button>
            )}
          </Toolbar>
          <Tabs
            value={currentTab}
            onChange={handleTabChange}
            centered
            sx={{
              backgroundColor: 'white',
              '& .MuiTab-root': { color: 'primary.main' },
              '& .Mui-selected': { color: 'primary.dark' }
            }}
          >
            <Tab label="My Closet" />
            <Tab label="Create Outfits" />
          </Tabs>
        </AppBar>
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          {currentTab === 0 ? (
            <>
              <Box sx={{ mb: 4 }}>
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Search your closet..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon />
                      </InputAdornment>
                    ),
                  }}
                />
              </Box>
              <ClothingList onEdit={handleEditItem} />
              <ClothingForm
                open={openForm}
                onClose={handleCloseForm}
                editItem={editItem}
              />
            </>
          ) : (
            <OutfitCreator />
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
