import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/OutfitCreator.css';

const OutfitCreator = () => {
  const [currentOutfit, setCurrentOutfit] = useState(0);
  const [outfits, setOutfits] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  const handleSwipe = (direction) => {
    if (direction === 'left') {
      setCurrentOutfit((prev) => (prev + 1) % outfits.length);
    } else {
      setCurrentOutfit((prev) => (prev - 1 + outfits.length) % outfits.length);
    }
  };

  const addItemToOutfit = (item) => {
    setSelectedItems([...selectedItems, item]);
  };

  const saveOutfit = () => {
    if (selectedItems.length > 0) {
      setOutfits([...outfits, { items: selectedItems }]);
      setSelectedItems([]);
    }
  };

  return (
    <div className="outfit-creator">
      <h2>Create Your Outfits</h2>

      <div className="outfit-display">
        <AnimatePresence mode="wait">
          {outfits.length > 0 && (
            <motion.div
              key={currentOutfit}
              initial={{ x: 300, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: -300, opacity: 0 }}
              transition={{ type: "spring", stiffness: 300, damping: 30 }}
              className="outfit-card"
            >
              <div className="outfit-items">
                {outfits[currentOutfit].items.map((item, index) => (
                  <div key={index} className="outfit-item">
                    <img src={item.image} alt={item.name} />
                    <p>{item.name}</p>
                  </div>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {outfits.length > 0 && (
          <div className="swipe-buttons">
            <button onClick={() => handleSwipe('right')}>←</button>
            <button onClick={() => handleSwipe('left')}>→</button>
          </div>
        )}
      </div>

      <div className="outfit-builder">
        <h3>Build New Outfit</h3>
        <div className="selected-items">
          {selectedItems.map((item, index) => (
            <div key={index} className="selected-item">
              <img src={item.image} alt={item.name} />
              <p>{item.name}</p>
            </div>
          ))}
        </div>
        <button 
          className="save-outfit-btn"
          onClick={saveOutfit}
          disabled={selectedItems.length === 0}
        >
          Save Outfit
        </button>
      </div>
    </div>
  );
};

export default OutfitCreator;
