import React, { useState } from 'react';
import { Container, Box, AppBar, Tabs, Tab, Button } from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import ClothingList from './components/ClothingList';
import OutfitCreator from './components/OutfitCreator';
import AuthModal from './components/Auth/AuthModal';
import { AuthProvider, useAuth } from './context/AuthContext';

const theme = createTheme({
  palette: {
    primary: {
      main: '#FF69B4',
    },
    secondary: {
      main: '#FFB6C1',
    },
  },
});

const AppContent = () => {
  const [tabValue, setTabValue] = useState(0);
  const [authModalOpen, setAuthModalOpen] = useState(false);
  const { user, logout } = useAuth();

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (!user) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          background: 'linear-gradient(135deg, #FFE4E1 0%, #FFF0F5 100%)',
        }}
      >
        <Button
          variant="contained"
          onClick={() => setAuthModalOpen(true)}
          sx={{
            background: 'linear-gradient(45deg, #FF69B4 30%, #FFB6C1 90%)',
            borderRadius: 20,
            boxShadow: '0 3px 5px 2px rgba(255, 105, 180, .3)',
            color: 'white',
            padding: '10px 30px',
            fontSize: '1.2rem',
          }}
        >
          Get Started
        </Button>
        <AuthModal open={authModalOpen} onClose={() => setAuthModalOpen(false)} />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ background: 'linear-gradient(45deg, #FF69B4 30%, #FFB6C1 90%)' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', px: 2 }}>
          <Tabs
            value={tabValue}
            onChange={handleTabChange}
            sx={{
              flexGrow: 1,
              '& .MuiTab-root': {
                color: 'rgba(255, 255, 255, 0.7)',
                '&.Mui-selected': {
                  color: '#fff',
                },
              },
            }}
          >
            <Tab label="My Closet" />
            <Tab label="Create Outfits" />
          </Tabs>
          <Button
            color="inherit"
            onClick={logout}
            sx={{
              ml: 2,
              borderRadius: 20,
              border: '1px solid white',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
              },
            }}
          >
            Logout
          </Button>
        </Box>
      </AppBar>

      <Container sx={{ mt: 4 }}>
        {tabValue === 0 ? <ClothingList /> : <OutfitCreator />}
      </Container>
    </Box>
  );
};

const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <AuthProvider>
        <AppContent />
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
